import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import { connect } from "react-redux";
import { compose } from "redux";
import io from "socket.io-client";

import CustomInput from "./CustomInput";
import * as actions from "../actions";
import CustomDropDown from "./CustomDropDown";

class Request extends Component {
  constructor(props) {
    super(props);
    this.onSubmit = this.onSubmit.bind(this);
    this.state = {
      socket: io.connect("http://localhost:5000")
    };
  }

  updateNeeded() {
    this.state.socket.emit("refresh", localStorage.getItem("JWT_TOKEN"));
  }

  async onSubmit(formData) {
    formData.date = new Date();
    await this.props.Request(formData);
    if (!this.props.errorMessage) {
      this.updateNeeded();
      this.props.history.push("/");
    }
  }

  render() {
    const { handleSubmit } = this.props;
    return (
      <div className="row">
        <div className="col">
          <form onSubmit={handleSubmit(this.onSubmit)}>
            <fieldset>
              <Field component={CustomDropDown} />
            </fieldset>
            <fieldset>
              <Field>
                <div className="select">
                  <select
                    name={this.props.id}
                    id={this.props.id}
                    type={this.props.type}
                    value={value}
                    onChange={onChange}
                  >
                    <option value="" disabled selected>
                      Select your action
                    </option>
                    <option value="Add">Add</option>
                    <option value="Edit">Edit</option>
                    <option value="Delete">Delete</option>
                  </select>
                  {console.log(this.props)}
                </div>
              </Field>
            </fieldset>
            {this.props.errorMessage ? (
              <div className="alert  alert-danger">
                {this.props.errorMessage}
              </div>
            ) : null}
            <button
              type="submit"
              className="button search"
              onClick={this.updateNeeded()}
            >
              Request
            </button>
          </form>
        </div>
      </div>
    );
  }
}

function MapStateToProps(state) {
  return {
    errorMessage: state.park.errorMessage
  };
}

export default compose(
  connect(
    MapStateToProps,
    actions
  ),
  reduxForm({ form: "Request" })
)(Request);

